import { useState, useEffect } from "react";
import "./App.css";


const api = {
  key: "d6a1242e4fb7d7b5d17cc1f071627167",
  base: "https://api.openweathermap.org/data/2.5/"
}


function App() {

  const [location, setLocation] = useState('New Delhi')
  const [locationInfo, setLocationInfo] = useState({})
  useEffect(() => {
    console.log(location, "place");
    handleFetch();
  }, []);



  const handleFetch = evt => {
    if (evt.key === "Enter") {
      fetch(`${api.base}weather?q=${location}&units=metric&APPID=${api.key}`)
        .then(res => res.json())
        .then(result => {
          setLocationInfo(result);
          setLocation('');
          console.log(result);
        });
    }
  }


  return <div className="container ">
    <main>
      <div className="search-box">
        <input type="text" className="search-input" placeholder="Search..." value={location} onChange={(e) => setLocation(e.target.value)} />
      </div>

      <div className="location-box">
        <div className="place">New Delhi</div>

        <div className="date">{new Date().toLocaleString() + ""}</div>
      </div>
      <div className="weather-box">
